@implementation VZInspectorMermoryManager
+ (instancetype)sharedInstance {
    static VZInspectorMermoryManager *_manager = nil;
    static dispatch_once_t onceToken;
    dispatch_once(&onceToken, ^{
        _manager = [[VZInspectorMermoryManager alloc] init];
    });
    return _manager;
}

- (void)startTrackingIfNeed{
    [[VZInspectorMermoryDataHelper sharedInstance] startTrackingIfNeed];
}

- (VZInspectorBizLogView *)logView{
    return self.mermoryView;
}


- (VZInspectorMermoryView *)mermoryView{
    if(!_mermoryView){
        _mermoryView = [[VZInspectorMermoryView alloc]initWithFrame:CGRectMake(0, 20, screen_width, KVZInspectorMermoryCellHeight * KVZInspectorMermoryCellCount + 44 + 30 + 36)];
        UIPanGestureRecognizer *pan = [[UIPanGestureRecognizer alloc] initWithTarget:self action:@selector(dragLogViewPanGesture:)];
        [_mermoryView addDragTagGestureRecognizer:pan];
    }
    return _mermoryView;
}

